import React from 'react'
import{useState} from 'react';
import "./Product.scss"
import AddShoppingCartIcon from"@mui/icons-material/AddShoppingCart";
import FavoriteBorderIcon from"@mui/icons-material/FavoriteBorder";
import BalanceIcon from"@mui/icons-material/Balance";


const Product = () => {

  const [selectedImg, setSelectedImg] = useState(0);
  const [quantity, setQuantity] = useState(1);

  const images = [
    "https://images.pexels.com/photos/10026491/pexels-photo-10026491.png?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
    "https://images.pexels.com/photos/12179283/pexels-photo-12179283.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
  ];

  return (
    <div className='product'>
      <div className="left">
        <div className="images">
          <img src={images[0]} alt="" onClick={e=>setSelectedImg(0)}/>
          <img src={images[1]} alt="" onClick={e=>setSelectedImg(1)}/>
        </div>
        <div className="mainImg">
          <img src={images[selectedImg]} alt="" />
        </div>
      </div>
      <div className="right">
        <h1>Title</h1>
        <span>$199</span>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. 
          Aperiam hic vitae quam ut, vero natus iusto ad distinctio,
          magnam cum quas accusamus reprehenderit commodi id illum aliquam 
          dolorum delectus qui?
        </p>
        <div className="quantity">
          <button onClick={() => setQuantity((prev) => prev === 1? 1: prev - 1)}>-</button>
          {quantity}
          <button onClick={() => setQuantity((prev) => prev + 1)}>+</button>
        </div>
      </div>
    </div>
  )
}

export default Product
