import React from 'react';
import "./Card.scss";
import {Link} from "react-router-dom";

const Card = ({item}) => {
  return (
    <Link className="link" to={`/product/${item.id}`}>
    <div className='card'>
        <div className="image">
            {item?.attributes.isNew && <span>New Season</span>}
            <img src={process.env.REACT_APP_UPLOAD_URL + item.img.data.attributes.url} alt="" className="mainImg" />
            <img src={process.env.REACT_APP_UPLOAD_URL + item.img2.data.attributes.url} alt="" className="secondImg" />
        </div>
        <h2>{item?.attributes.title}</h2>
        <div className="prices">
            <h3>${item.oldPrice || item?.attributes.price + 20}</h3>   {/*if there is not oldPrice then add $20 more dollars} */}
            <h3>${item?.attributes.price}</h3>
        </div>
        </div>   
    </Link>
  )
}

export default Card
