import React from 'react'
import { useState } from 'react';
import "./FeaturedProducts.scss"
import Card from '../Card/Card'
import { useEffect } from 'react';
import axios from 'axios';

const FeaturedProducts = ({type}) => {

   
    const [data, setData] = useState([]);

    useEffect(()=>{
        const fetchData =async ()=>{
        try {
            const res = await axios.get(
                process.env.REACT_APP_API_URL + `/products?populate=*&[filters][type][$eq]=`,  //populate and all, one more query filters. This will grab the images from the database
                {
                    headers: {
                        Authorization: `bearer ${process.env.REACT_APP_API_TOKEN}`,
                    },
                }
            );
            setData(res.data.data)
        }   catch (err) {
            console.log(err);
            }
        };
        fetchData();
    }, []);

    
  return (
    <div className='featuredProducts'>
        <div className="top">
            <h1>{type} products</h1>
            <p>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. 
                Amet voluptate nesciunt ad labore a quas odit, doloremque 
                asperiores perferendis sequi laboriosam adipisci possimus 
                et nobis modi ex ullam enim ea?
            </p>
        </div>
        <div className="bottom">
            {data.map(item=>(
                <Card item={item} key={item.id}/>
            ))}
        </div>
    </div>
  )
}

export default FeaturedProducts
