import React from 'react';
import "./Card.scss";

const Card = ({item}) => {
  return (
    <Link className="link" to={`/product/${item.id}`}>
    <div className='card'>
        <img src={item.img} alt="mainImg" />
        <img src={item.img2} alt="secondImg" />
    </div>
    <h2>{item.title}</h2>
    <div className="prices">
        <h3>${item.oldPrice}</h3>
        <h3>${item.price}</h3>
    </div>
    </Link>
  )
}

export default Card
